HERROR MGR_MHEGIB_RequestFileOrData(HUINT16	 	 usSessionId,
									HUINT32	 	 nReqType,
									HUINT32		 ulReqNameLen,
									HUINT8		*paucReqName)
{
#if defined(CONFIG_SUPPORT_MHEG_IB)
	return pmgr_mhegib_RequestFileOrData(usSessionId, nReqType, ulReqNameLen, paucReqName);
#else
	return ERR_FAIL;
#endif
}

HERROR MGR_MHEGIB_RequestAppAbort(HUINT16 usSessionId, HUINT32 nAbortReqCode)
{
#if defined(CONFIG_SUPPORT_MHEG_IB)
	return pmgr_mhegib_RequestAppAbort(usSessionId, nAbortReqCode);
#else
	return ERR_FAIL;
#endif
}

