void BCHP_PWR_P_HW_PLL_AVD_Control(BCHP_Handle handle, bool activate)
{
    uint32_t mask;

    BDBG_MSG(("HW_PLL_AVD: %s", activate?"on":"off"));

    if (activate) {
	uint32_t reg, cnt=50;
	/* power up PLL_AVD */
	mask = BCHP_CLKGEN_PLL_AVD1_PLL_PWRDN_PWRDN_PLL_MASK;
	BREG_AtomicUpdate32(handle->regHandle, BCHP_CLKGEN_PLL_AVD1_PLL_PWRDN , mask, 0);

	mask = BCHP_CLKGEN_PLL_AVD1_PLL_RESET_RESETD_MASK;
	BREG_AtomicUpdate32(handle->regHandle, BCHP_CLKGEN_PLL_AVD1_PLL_RESET, mask, 0);

	/* Check for PLL lock */
	while(cnt--) {
	    BKNI_Delay(10);
	    reg = BREG_Read32(handle->regHandle, BCHP_CLKGEN_PLL_AVD1_PLL_LOCK_STATUS);
	    if (BCHP_GET_FIELD_DATA(reg, CLKGEN_PLL_AVD1_PLL_LOCK_STATUS, LOCK))
		break;
	}

	if(!cnt)
	    BDBG_ERR(("AVD PLL Lock Timeout"));
    }
	else {
	/* power down PLL_AVD */
	mask = BCHP_CLKGEN_PLL_AVD1_PLL_RESET_RESETD_MASK;
	BREG_AtomicUpdate32(handle->regHandle, BCHP_CLKGEN_PLL_AVD1_PLL_RESET, mask, mask);

	mask = BCHP_CLKGEN_PLL_AVD1_PLL_PWRDN_PWRDN_PLL_MASK;
	BREG_AtomicUpdate32(handle->regHandle, BCHP_CLKGEN_PLL_AVD1_PLL_PWRDN , mask, mask);
    }
}

